com.mirth.connect.plugins.javascriptstep.script:
// =========================
// 1. WYCIĄGANIE DANYCH Z HL7
// =========================
var msh = msg['MSH'];
var pid = msg['PID'];
var obr = msg['OBR'];
var obx = msg['OBX'];

// ServiceRequest i Patient
var serviceRequestId = obr['OBR.2']['OBR.2.1'].toString();
var patientId        = pid['PID.3']['PID.3.1'].toString();
var familyName       = pid['PID.5']['PID.5.1'].toString();
var givenName        = pid['PID.5']['PID.5.2'].toString();
var prefix           = pid['PID.5']['PID.5.3'] ? pid['PID.5']['PID.5.3'].toString() : "";
var birthDate        = pid['PID.7']['PID.7.1'].toString();
var gender           = pid['PID.8']['PID.8.1'].toString();
var phone            = pid['PID.13'] && pid['PID.13']['PID.13.1'] ? pid['PID.13']['PID.13.1'].toString() : "";
var addressLine      = pid['PID.11']['PID.11.1'].toString();
var city             = pid['PID.11']['PID.11.3'].toString();
var postalCode       = pid['PID.11']['PID.11.5'].toString();
var country          = pid['PID.11']['PID.11.6'].toString();
var mrn              = pid['PID.3']['PID.3.1'].toString(); // lub PID.3.2 w zależności od mapowania

// Observation
var observationCode  = obr['OBR.4']['OBR.4.1'].toString();
var reportText       = obx['OBX.5']['OBX.5.1'].toString().replace(/\\.br\\/g,"\n");

// Konwersja płci HL7 → FHIR
if (gender == 'M') gender = 'male';
else if (gender == 'F') gender = 'female';
else gender = 'unknown';

// Format daty YYYYMMDD → YYYY-MM-DD
if (birthDate && birthDate.length == 8) {
    birthDate = birthDate.substring(0,4) + "-" +
                birthDate.substring(4,6) + "-" +
                birthDate.substring(6,8);
}

// =========================
// 2. WKŁADAMY DO CHANNELMAP
// =========================
channelMap.put('serviceRequestId', serviceRequestId);
channelMap.put('patientId', patientId);
channelMap.put('familyName', familyName);
channelMap.put('givenName', givenName);
channelMap.put('prefix', prefix);
channelMap.put('birthDate', birthDate);
channelMap.put('gender', gender);
channelMap.put('phone', phone);
channelMap.put('addressLine', addressLine);
channelMap.put('city', city);
channelMap.put('postalCode', postalCode);
channelMap.put('country', country);
channelMap.put('mrn', mrn);
channelMap.put('observationCode', observationCode);
channelMap.put('reportText', reportText);

// =========================
// 3. BUDOWANIE FHIR JSON
// =========================
var observation = {
    resourceType: "Observation",
    status: "final",
    category: [{
        coding: [{
            system: "http://terminology.hl7.org/CodeSystem/observation-category",
            code: "imaging",
            display: "Imaging"
        }]
    }],
    code: { coding: [{ system: "http://example.org/riscodes", code: observationCode }] },
    subject: { reference: "Patient/" + patientId, display: familyName + " " + givenName },
    basedOn: [{ reference: "ServiceRequest/" + serviceRequestId }],
    effectiveDateTime: DateUtil.getCurrentDate("yyyy-MM-dd'T'HH:mm:ss"),
    valueString: reportText
};

var status = {
    resourceType: "ServiceRequest",
    id: serviceRequestId,
    status: "completed"
};

// JSON do channelMap
channelMap.put('observation', JSON.stringify(observation));
channelMap.put('status', JSON.stringify(status));

properties.script:
// 1. Wczytaj plik PDF
var FileReader = Packages.java.io.FileInputStream;
var file = new FileReader("/opt/connect/test.pdf"); // ścieżka w kontenerze
var bytes = java.lang.reflect.Array.newInstance(java.lang.Byte.TYPE, file.available());
file.read(bytes);
file.close();

// 2. Zamień na Base64
var Base64 = Packages.java.util.Base64;
var encoded = Base64.getEncoder().encodeToString(bytes);

// 3. Stwórz JSON do wysłania
var json = {
    "Parent": $('studyId'),
    "Tags": {"Modality": "OT"},
    "Content": "data:application/pdf;base64," + encoded
};

// 4. Wyślij do Orthanc przez HTTP POST
var url = "http://192.168.1.38:8042/tools/create-dicom";
var client = new Packages.org.apache.commons.httpclient.HttpClient();
var post = new Packages.org.apache.commons.httpclient.methods.PostMethod(url);

post.addRequestHeader("Content-Type", "application/json");
post.setRequestBody(JSON.stringify(json));

var status = client.executeMethod(post);
var response = post.getResponseBodyAsString();

logger.info("HTTP status: " + status);
logger.info("Response: " + response);

post.releaseConnection();

channel.postprocessingScript:
